import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Card from './components/Card';

function App() {
  const [isNavbarVisible, setIsNavbarVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [isLinkClicked, setIsLinkClicked] = useState(false);

  useEffect(() => {
    const controlNavbar = () => {
      if (!isLinkClicked) {
        if (typeof window !== 'undefined') {
          if (window.scrollY > lastScrollY + 30) { // Increase the threshold for hiding the navbar
            setIsNavbarVisible(false);
          } else if (window.scrollY < lastScrollY) { // Increase the threshold for showing the navbar
            setIsNavbarVisible(true);
          }
          // remember current page location to use in the next move
          setLastScrollY(window.scrollY);
        }
      } else {
        setIsLinkClicked(false); // reset the link click state
      }
    };

    if (typeof window !== 'undefined') {
      window.addEventListener('scroll', controlNavbar);

      // cleanup function
      return () => {
        window.removeEventListener('scroll', controlNavbar);
      };
    }
  }, [lastScrollY, isLinkClicked]);

  const handleLinkClick = () => {
    setIsLinkClicked(true);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Navbar isVisible={isNavbarVisible} onLinkClick={handleLinkClick} />
      <section id="home">
        <Hero />
      </section>
      <section id="trending" className="container mx-auto px-4 py-4">
        <Card />
      </section>
      <section id="discover" className="container mx-auto px-4 py-16">
        {/* Add Discover component content */}
      </section>
      <section id="playlists" className="container mx-auto px-4 py-16">
        {/* Add Playlists component content */}
      </section>
      <section id="artists" className="container mx-auto px-4 py-16">
        {/* Add Artists component content */}
      </section>
    </div>
  );
}

export default App;
