import React, { useState, useCallback, useRef, useEffect } from 'react';
import axios from 'axios';
import { getSpotifyToken, getLastFmArtistDetails, getLastFmTrackDetails } from './API';
import ArtistCard from './ArtistCard';
import TrackCard from './TrackCard';
import ArtistModal from './ArtistModal';
import TrackModal from './TrackModal';

const Discover = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [selectedTrack, setSelectedTrack] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const modalRef = useRef(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const token = await getSpotifyToken();
      const response = await axios.get('https://api.spotify.com/v1/search', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        params: {
          q: searchQuery,
          type: 'artist,track,album',
          limit: 10
        }
      });

      setSearchResults(response.data);
    } catch (err) {
      setError('Failed to fetch search results');
    } finally {
      setLoading(false);
    }
  };

  const handleArtistClick = useCallback(async (artist) => {
    setSelectedArtist(artist);
    try {
      const artistDetails = await getLastFmArtistDetails(artist.name);
      setSelectedArtist((prev) => ({ ...prev, artistDetails }));
    } catch (err) {
      console.error('Failed to fetch artist details', err);
    }
    setIsModalVisible(true);
  }, []);

  const handleTrackClick = useCallback(async (track) => {
    setSelectedTrack(track);
    try {
      const trackDetails = await getLastFmTrackDetails(track.id);
      setSelectedTrack((prev) => ({ ...prev, trackDetails }));
    } catch (err) {
      console.error('Failed to fetch track details', err);
    }
    setIsModalVisible(true);
  }, []);

  const closePopup = useCallback(() => {
    setIsModalVisible(false);
    setTimeout(() => {
      setSelectedArtist(null);
      setSelectedTrack(null);
    }, 300);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closePopup();
      }
    };

    if (isModalVisible) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isModalVisible, closePopup]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <h1 className="text-4xl font-bold mb-6">Discover Music</h1>
      <form onSubmit={handleSearch} className="w-full max-w-lg flex items-center bg-white rounded-lg shadow-md mb-4">
        <div className="px-3">
          <svg className="h-6 w-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
          </svg>
        </div>
        <input
          type="text"
          className="flex-grow p-2 rounded-l-lg focus:outline-none"
          placeholder="Search for artists, songs, or albums..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button type="submit" className="bg-red-600 text-white px-4 py-2 rounded-r-lg">Search</button>
      </form>
      {loading && <p className="mt-4">Loading...</p>}
      {error && <p className="mt-4 text-red-500">{error}</p>}
      <div className="w-full max-w-4xl grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {searchResults.artists?.items?.map((artist) => (
          <div onClick={() => handleArtistClick(artist)} key={artist.id}>
            <ArtistCard artist={artist} hideStats={true} />
          </div>
        ))}
        {searchResults.tracks?.items?.map((track) => (
          <div onClick={() => handleTrackClick(track)} key={track.id}>
            <TrackCard track={track} hideStats={true} />
          </div>
        ))}
        {searchResults.albums?.items?.map((album) => (
          <iframe
            key={album.id}
            src={`https://open.spotify.com/embed/album/${album.id}`}
            width="100%"
            height="380"
            frameBorder="0"
            allowTransparency="true"
            allow="encrypted-media"
            title={album.name}
            className="rounded-lg shadow-md"
          ></iframe>
        ))}
      </div>
      {selectedArtist && <ArtistModal artist={selectedArtist} isVisible={isModalVisible} onClose={closePopup} modalRef={modalRef} />}
      {selectedTrack && <TrackModal track={selectedTrack} isVisible={isModalVisible} onClose={closePopup} modalRef={modalRef} />}
    </div>
  );
};

export default Discover;
