// src/components/Card.js

import React, { useEffect, useState } from 'react';
import { getTopTracks } from '../utils/spotify';

const Card = () => {
  const [tracks, setTracks] = useState([]);

  useEffect(() => {
    const fetchTracks = async () => {
      const topTracks = await getTopTracks();
      setTracks(topTracks);
    };

    fetchTracks();
  }, []);

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
      {tracks.map((track, index) => (
        <div key={index} className="bg-white shadow-md rounded-lg overflow-hidden">
          <img src={track.track.album.images[0].url} alt={track.track.name} className="w-full h-48 object-cover" />
          <div className="p-4">
            <h3 className="text-lg font-bold">{track.track.name}</h3>
            <p className="text-gray-700">{track.track.artists.map(artist => artist.name).join(', ')}</p>
            <p className="text-gray-700">Streams: {track.track.popularity}</p> {/* Note: Spotify API does not provide exact stream counts */}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Card;
