import React, { useEffect, useState } from 'react';
import { getTopTracks } from '../utils/spotify';

const Card = () => {
  const [tracks, setTracks] = useState([]);

  useEffect(() => {
    const fetchTracks = async () => {
      const topTracks = await getTopTracks();
      setTracks(topTracks);
    };

    fetchTracks();
  }, []);

  const topSixTracks = tracks.slice(0, 6);
  const remainingTracks = tracks.slice(6);

  return (
    <div className="p-4 flex">
      {/* Top 6 tracks */}
      <div className="w-2/3 pr-4 grid grid-cols-3 gap-4">
        {topSixTracks.map((track, index) => (
          <div key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
            <img src={track.track.album.images[0].url} alt={track.track.name} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h3 className="font-bold text-lg mb-2 truncate">{track.track.name}</h3>
              <p className="text-sm text-gray-600 truncate">{track.track.artists.map(artist => artist.name).join(', ')}</p>
              <p className="text-sm text-gray-500 mt-2">Popularity: {track.track.popularity}%</p>
            </div>
          </div>
        ))}
      </div>

      {/* Remaining tracks table */}
      <div className="w-1/3 pl-4">
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <table className="w-full">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2 text-left">Name</th>
                <th className="px-4 py-2 text-left">Artists</th>
                <th className="px-4 py-2 text-right">Popularity</th>
              </tr>
            </thead>
            <tbody>
              {remainingTracks.map((track, index) => (
                <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                  <td className="px-4 py-2 truncate">{track.track.name}</td>
                  <td className="px-4 py-2 truncate">{track.track.artists.map(artist => artist.name).join(', ')}</td>
                  <td className="px-4 py-2 text-right">{track.track.popularity}%</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Card;
