// src/App.js

import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Card from './components/Card';

function App() {
  const [isNavbarVisible, setIsNavbarVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  useEffect(() => {
    const controlNavbar = () => {
      if (typeof window !== 'undefined') {
        if (window.scrollY > lastScrollY + 25) { // Increase the threshold for hiding the navbar
          setIsNavbarVisible(false);
        } else if (window.scrollY < lastScrollY - 1) { // Increase the threshold for showing the navbar
          setIsNavbarVisible(true);
        }

        // remember current page location to use in the next move
        setLastScrollY(window.scrollY);
      }
    };

    if (typeof window !== 'undefined') {
      window.addEventListener('scroll', controlNavbar);

      // cleanup function
      return () => {
        window.removeEventListener('scroll', controlNavbar);
      };
    }
  }, [lastScrollY]);

  return (
    <div className="min-h-screen bg-background text-text">
      <Navbar isVisible={isNavbarVisible} />
      <section id="home" className="section h-30 p-4"> {/* Reduced height */}
        <Hero />
      </section>
      <section id="trending" className="section p-4">
        <div className="container mx-auto px-4 py-16">
          <Card />
        </div>
      </section>
      <section id="discover" className="section h-24 p-4">
        <h1 className="text-5xl font-bold text-center mb-4">Discover</h1>
        {/* Discover section content */}
      </section>
      <section id="playlists" className="section h-24 p-4">
        <h1 className="text-5xl font-bold text-center mb-4">Playlists</h1>
        {/* Playlists section content */}
      </section>
      <section id="artists" className="section h-24 p-4">
        <h1 className="text-5xl font-bold text-center mb-4">Artists</h1>
        {/* Artists section content */}
      </section>
    </div>
  );
}

export default App;
