import React from 'react';

const TrackCard = ({ track, index, onClick }) => {
  const formatNumber = (num) => num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");

  return (
    <div
      key={track.id}
      className="bg-white rounded-lg shadow-md overflow-hidden cursor-pointer transition-all duration-150 ease-in-out transform hover:-translate-y-1 hover:shadow-lg hover:shadow-glow hover:bg-gray-100"
      onClick={() => onClick(track)}
      style={{ padding: '0.5rem' }}
    >
      <div className="w-full h-48 overflow-hidden flex justify-center items-center"> {/* Fixed height and center the image */}
        <img src={track.image} alt={track.name} className="w-full h-full object-cover" loading="lazy" style={{ width: '150px', height: '150px' }} /> {/* Fixed width and height */}
      </div>
      <div className="p-1">
        <h3 className="font-bold text-md mb-1 truncate">
          <span className="mr-1">{index + 1}.</span>{track.name}
        </h3>
        <p className="text-sm text-red-600 truncate">{track.artist}</p>
        <p className="text-xs text-black-500 mt-1">Streams: {formatNumber(track.playcount)}</p>
      </div>
    </div>
  );
};

export default TrackCard;
