import React, { useState, useEffect } from 'react';
import axios from 'axios';

const clientId = '7c6b8a3ea0e3406b9d8e5974be8b442b';
const clientSecret = '49aa02d0a85249279ae8ab73bbef28f5';

const getSpotifyToken = async () => {
  const response = await axios.post('https://accounts.spotify.com/api/token', null, {
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      'Authorization': `Basic ${btoa(`${clientId}:${clientSecret}`)}`
    },
    params: {
      grant_type: 'client_credentials'
    }
  });

  return response.data.access_token;
};

const getSpotifyTopTracks = async () => {
  const token = await getSpotifyToken();
  const response = await axios.get('https://api.spotify.com/v1/playlists/37i9dQZEVXbMDoHDwVN2tF/tracks', {
    headers: {
      'Authorization': `Bearer ${token}`
    },
    params: {
      market: 'US',
      limit: 25
    }
  });

  return response.data.items.map(item => ({
    name: item.track.name,
    artist: item.track.artists[0].name,
    image: item.track.album.images[1]?.url || '',
  }));
};

const getLastFmStreams = async (track, artist) => {
  try {
    const response = await axios.get('http://ws.audioscrobbler.com/2.0/', {
      params: {
        method: 'track.getInfo',
        api_key: '28c425ac5fe3a7288e17efe8d7f9f82c', // Replace with your Last.fm API key
        artist: artist,
        track: track,
        format: 'json'
      }
    });
    if (response.data && response.data.track) {
      return response.data.track.playcount;
    }
  } catch (err) {
    console.error('Failed to fetch Last.fm streams', err);
  }
  return 'N/A';
};

const Card = () => {
  const [tracks, setTracks] = useState([]);
  const [error, setError] = useState('');
  const [selectedTrack, setSelectedTrack] = useState(null);

  useEffect(() => {
    const fetchTracks = async () => {
      try {
        const spotifyTracks = await getSpotifyTopTracks();

        const tracksWithStreams = await Promise.all(spotifyTracks.map(async (spotifyTrack) => {
          const playcount = await getLastFmStreams(spotifyTrack.name, spotifyTrack.artist);
          return {
            ...spotifyTrack,
            playcount
          };
        }));

        setTracks(tracksWithStreams);
        setError('');
      } catch (err) {
        setError('Failed to fetch top songs');
      }
    };
    fetchTracks();
  }, []);

  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const handleTrackClick = (track) => {
    setSelectedTrack(track);
  };

  const closePopup = () => {
    setSelectedTrack(null);
  };

  const topSixTracks = tracks.slice(0, 6);
  const remainingTracks = tracks.slice(6, 19); // Limit to 15 more tracks

  return (
    <div className="p-4">
      <h1 className="text-5xl font-bold text-center mb-4 text-red-600">Trending</h1>
      <div className="flex">
        {/* Top 6 tracks */}
        <div className="w-1/2 pr-4 grid grid-cols-3 gap-4">
          {topSixTracks.map((track, index) => (
            <div 
              key={index} 
              className="bg-white rounded-lg shadow-md overflow-hidden cursor-pointer transition-all duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-lg hover:bg-gray-100"
              onClick={() => handleTrackClick(track)}
            >
              <img src={track.image} alt={track.name} className="w-full h-48 object-cover" />
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2 truncate">
                  <span className="mr-2">{index + 1}.</span>{track.name}
                </h3>
                <p className="text-sm text-red-600 truncate">{track.artist}</p>
                <p className="text-sm text-black-500 mt-2">Streams: {formatNumber(track.playcount)}</p>
              </div>
            </div>
          ))}
        </div>
        {/* Remaining tracks table */}
        <div className="w-1/2 pl-4">
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <table className="w-full">
              <thead className="bg-zinc-900/100 text-accent">
                <tr>
                  <th className="px-4 py-2 text-left">#</th>
                  <th className="px-4 py-2 text-left"></th>
                  <th className="px-4 py-2 text-left">Name</th>
                  <th className="px-4 py-2 text-left">Artist</th>
                  <th className="px-4 py-2 text-right">Streams</th>
                </tr>
              </thead>
              <tbody>
                {remainingTracks.map((track, index) => (
                  <tr 
                    key={index} 
                    className={`${index % 2 === 0 ? 'bg-primary/20' : 'bg-accent/10'} hover:bg-gray-200 cursor-pointer transition-colors duration-300`}
                    onClick={() => handleTrackClick(track)}
                  >
                    <td className="px-4 py-2">{index + 7}</td>
                    <td className="px-4 py-2">
                      <img src={track.image} alt={track.name} className="w-8 h-8 object-cover" />
                    </td>
                    <td className="px-4 py-2 truncate">{track.name}</td>
                    <td className="px-4 py-2 truncate">{track.artist}</td>
                    <td className="px-4 py-2 text-right">{formatNumber(track.playcount)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      {error && <p className="text-red-500 text-center mt-4">{error}</p>}

      {/* Pop-up menu for displaying stats */}
      {selectedTrack && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg shadow-xl max-w-md">
            <h2 className="text-2xl font-bold mb-4">{selectedTrack.name}</h2>
            <img src={selectedTrack.image} alt={selectedTrack.name} className="w-full h-64 object-cover mb-4 rounded" />
            <p className="text-lg mb-2">Artist: {selectedTrack.artist}</p>
            <p className="text-lg mb-4">Streams: {formatNumber(selectedTrack.playcount)}</p>
            <button 
              onClick={closePopup}
              className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition-colors duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Card;
