import React from 'react';

const TrackTable = ({ tracks, onClick }) => {
  const formatNumber = (num) => num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden h-full">
      <table className="w-full table-fixed">
        <thead className="bg-zinc-900/100 text-accent">
          <tr>
            <th className="w-12 px-4 py-2 text-left">#</th>
            <th className="w-16 px-4 py-2 text-left"></th>
            <th className="w-1/4 px-4 py-2 text-left">Name</th>
            <th className="w-1/4 px-4 py-2 text-left">Artist</th>
            <th className="w-1/4 px-4 py-2 text-right">Streams</th>
          </tr>
        </thead>
        <tbody className="overflow-y-auto" style={{ maxHeight: '500px', display: 'block' }}>
          {tracks.map((track, index) => (
            <tr
              key={track.id}
              className={`${index % 2 === 0 ? 'bg-primary/20' : 'bg-accent/10'} hover:bg-gray-200 cursor-pointer transition-colors duration-300`}
              onClick={() => onClick(track)}
              style={{ display: 'table', tableLayout: 'fixed', width: '100%' }}
            >
              <td className="w-12 px-4 py-2">{index + 7}</td>
              <td className="w-16 px-4 py-2">
                <img src={track.image} alt={track.name} className="w-8 h-8 object-cover" loading="lazy" />
              </td>
              <td className="w-1/4 px-4 py-2 truncate">{track.name}</td>
              <td className="w-1/4 px-4 py-2 truncate">{track.artist}</td>
              <td className="w-1/4 px-4 py-2 text-right">{formatNumber(track.playcount)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TrackTable;
