// src/components/Card.js

import React, { useEffect, useState } from 'react';
import { getTopTracks } from '../utils/spotify';

const Card = () => {
  const [tracks, setTracks] = useState([]);

  useEffect(() => {
    const fetchTracks = async () => {
      const topTracks = await getTopTracks();
      setTracks(topTracks);
    };

    fetchTracks();
  }, []);

  return (
    <div className="p-4">
      <div className="overflow-y-auto h-[600px]">
        <table className="min-w-full bg-white shadow-md rounded-lg">
          <thead>
            <tr>
              <th className="px-4 py-2 border-b-2 border-gray-200">Cover</th>
              <th className="px-4 py-2 border-b-2 border-gray-200">Name</th>
              <th className="px-4 py-2 border-b-2 border-gray-200">Artists</th>
              <th className="px-4 py-2 border-b-2 border-gray-200">Popularity</th>
            </tr>
          </thead>
          <tbody>
            {tracks.map((track, index) => (
              <tr key={index} className="border-b border-gray-200">
                <td className="px-4 py-2">
                  <img src={track.track.album.images[0].url} alt={track.track.name} className="w-12 h-12 object-cover rounded" />
                </td>
                <td className="px-4 py-2">{track.track.name}</td>
                <td className="px-4 py-2">{track.track.artists.map(artist => artist.name).join(', ')}</td>
                <td className="px-4 py-2">{track.track.popularity}</td> {/* Note: Spotify API does not provide exact stream counts */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Card;
