import React, { useState, useEffect, useRef } from 'react';
import { getSpotifyTopTracks, getLastFmStreams } from './API';

const Card = () => {
  const [tracks, setTracks] = useState([]);
  const [error, setError] = useState('');
  const [selectedTrack, setSelectedTrack] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const modalRef = useRef(null);

  useEffect(() => {
    const fetchTracks = async () => {
      try {
        const spotifyTracks = await getSpotifyTopTracks();

        const tracksWithStreams = await Promise.all(spotifyTracks.map(async (spotifyTrack) => {
          const playcount = await getLastFmStreams(spotifyTrack.name, spotifyTrack.artist);
          return {
            ...spotifyTrack,
            playcount
          };
        }));

        setTracks(tracksWithStreams);
        setError('');
      } catch (err) {
        setError('Failed to fetch top songs');
      }
    };
    fetchTracks();
  }, []);

  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const handleTrackClick = (track) => {
    setSelectedTrack(track);
    setIsModalVisible(true);
  };

  const closePopup = () => {
    setIsModalVisible(false);
    setTimeout(() => {
      setSelectedTrack(null);
    }, 300); // Match this duration with the transition duration
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closePopup();
      }
    };

    if (isModalVisible) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isModalVisible]);

  const topSixTracks = tracks.slice(0, 6);
  const remainingTracks = tracks.slice(6, 19);

  return (
    <div className="p-4">
      <h1 className="text-5xl font-bold text-center mb-4 text-red-600">Trending</h1>
      <div className="flex">
        <div className="w-1/2 pr-4 grid grid-cols-3 gap-4">
          {topSixTracks.map((track, index) => (
            <div 
              key={index} 
              className="bg-white rounded-lg shadow-md overflow-hidden cursor-pointer transition-all duration-150 ease-in-out transform hover:-translate-y-1 hover:shadow-lg hover:shadow-glow hover:bg-gray-100"
              onClick={() => handleTrackClick(track)}
            >
              <img src={track.image} alt={track.name} className="w-full h-auto object-contain" />
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2 truncate">
                  <span className="mr-2">{index + 1}.</span>{track.name}
                </h3>
                <p className="text-sm text-red-600 truncate">{track.artist}</p>
                <p className="text-sm text-black-500 mt-2">Streams: {formatNumber(track.playcount)}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="w-1/2 pl-4">
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <table className="w-full">
              <thead className="bg-zinc-900/100 text-accent">
                <tr>
                  <th className="px-4 py-2 text-left">#</th>
                  <th className="px-4 py-2 text-left"></th>
                  <th className="px-4 py-2 text-left">Name</th>
                  <th className="px-4 py-2 text-left">Artist</th>
                  <th className="px-4 py-2 text-right">Streams</th>
                </tr>
              </thead>
              <tbody>
                {remainingTracks.map((track, index) => (
                  <tr 
                    key={index} 
                    className={`${index % 2 === 0 ? 'bg-primary/20' : 'bg-accent/10'} hover:bg-gray-200 cursor-pointer transition-colors duration-300`}
                    onClick={() => handleTrackClick(track)}
                  >
                    <td className="px-4 py-2">{index + 7}</td>
                    <td className="px-4 py-2">
                      <img src={track.image} alt={track.name} className="w-8 h-8 object-contain" />
                    </td>
                    <td className="px-4 py-2 truncate">{track.name}</td>
                    <td className="px-4 py-2 truncate">{track.artist}</td>
                    <td className="px-4 py-2 text-right">{formatNumber(track.playcount)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      {error && <p className="text-red-500 text-center mt-4">{error}</p>}

      {selectedTrack && (
        <div className={`fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50 transition-opacity duration-300 ${isModalVisible ? 'opacity-100' : 'opacity-0'}`}>
          <div ref={modalRef} className={`bg-white p-6 rounded-lg shadow-xl w-full max-w-3xl relative transform transition-transform duration-300 ${isModalVisible ? 'translate-y-0' : '-translate-y-full'}`}>
            <button 
              onClick={closePopup}
              className="absolute top-2 right-2 text-gray-600 hover:text-gray-800"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
            <div className="flex">
              <div className="w-1/2 pr-4">
                <h2 className="text-2xl font-bold mb-2">{selectedTrack.name}</h2>
                <p className="text-lg mb-2">Artist: {selectedTrack.artist}</p>
                <p className="text-lg mb-2">Album: [Album Name Placeholder]</p>
                <p className="text-lg mb-4">Streams: {formatNumber(selectedTrack.playcount)}</p>
                <img src={selectedTrack.image} alt={selectedTrack.name} className="w-full h-auto object-contain mb-4 rounded" />
              </div>
              <div className="w-1/2 pl-4">
                <div className="mb-4">
                  <img src="[Artist Image Placeholder]" alt={selectedTrack.artist} className="w-32 h-32 rounded-full mx-auto" />
                </div>
                <h3 className="text-xl font-semibold mb-2">Additional Stats</h3>
                <ul className="list-disc pl-5">
                  <li>Release Date: [Placeholder]</li>
                  <li>Duration: [Placeholder]</li>
                  <li>Genre: [Placeholder]</li>
                  <li>Popularity: [Placeholder]</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Card;
