import React, { useState, useEffect } from 'react';
import { QueryClient, QueryClientProvider } from 'react-query';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Trending from './components/Trending';

// Create a client
const queryClient = new QueryClient();

function App() {
  const [isNavbarVisible, setIsNavbarVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [currentSection, setCurrentSection] = useState('Home');

  useEffect(() => {
    const controlNavbar = () => {
      if (typeof window !== 'undefined') {
        if (window.scrollY > lastScrollY + 30) {
          setIsNavbarVisible(false);
        } else if (window.scrollY < lastScrollY) {
          setIsNavbarVisible(true);
        }
        setLastScrollY(window.scrollY);
      }
    };

    if (typeof window !== 'undefined') {
      window.addEventListener('scroll', controlNavbar);

      return () => {
        window.removeEventListener('scroll', controlNavbar);
      };
    }
  }, [lastScrollY]);

  const handleLinkClick = (section) => {
    setCurrentSection(section);
  };

  return (
    <QueryClientProvider client={queryClient}>
      <div className="min-h-screen bg-background text-text">
        <Navbar isVisible={isNavbarVisible} onLinkClick={handleLinkClick} />
        {currentSection === 'Home' && (
          <section id="home">
            <Hero />
            <div className="flex justify-center">
              <Trending />
            </div>
          </section>
        )}
        {currentSection === 'Trending' && (
          <section id="trending" className="container mx-auto container-padding py-2">
            <div className="flex justify-center">
              <Trending />
            </div>
          </section>
        )}
        {currentSection === 'Discover' && (
          <section id="discover" className="container mx-auto container-padding section-padding">
            {/* Add Discover component content */}
          </section>
        )}
        {currentSection === 'Playlists' && (
          <section id="playlists" className="container mx-auto container-padding section-padding">
            {/* Add Playlists component content */}
          </section>
        )}
        {currentSection === 'Artists' && (
          <section id="artists" className="container mx-auto container-padding section-padding">
            {/* Add Artists component content */}
          </section>
        )}
      </div>
    </QueryClientProvider>
  );
}

export default App;
