import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useQuery } from 'react-query';
import { getLastFmTopArtists } from './API';
import ArtistCard from './ArtistCard';
import ArtistModal from './ArtistModal';

const Artists = () => {
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const modalRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(1);
  const artistsPerPage = 10;

  const { data: artists, error, isLoading } = useQuery('topArtists', getLastFmTopArtists, {
    staleTime: Infinity,
    cacheTime: Infinity,
  });

  const handleArtistClick = useCallback((artist) => {
    setSelectedArtist(artist);
    setIsModalVisible(true);
  }, []);

  const closePopup = useCallback(() => {
    setIsModalVisible(false);
    setTimeout(() => {
      setSelectedArtist(null);
    }, 300);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closePopup();
      }
    };

    if (isModalVisible) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isModalVisible, closePopup]);

  if (error) return <p className="text-red-500 text-center mt-4">Failed to fetch top artists</p>;
  if (isLoading) return <p className="text-center mt-4">Loading...</p>;
  if (!artists) return null;

  const sortedArtists = [...artists].sort((a, b) => b.popularity - a.popularity);
  const totalPages = Math.ceil(sortedArtists.length / artistsPerPage);
  const indexOfLastArtist = currentPage * artistsPerPage;
  const indexOfFirstArtist = indexOfLastArtist - artistsPerPage;
  const currentArtists = sortedArtists.slice(indexOfFirstArtist, indexOfLastArtist);

  const paginate = (pageNumber) => {
    setCurrentPage(Math.max(1, Math.min(pageNumber, totalPages)));
  };

  return (
    <div className="p-4 mb-3">
      <h1 className="text-5xl font-bold text-center mb-5 text-red-600 py-3">Top Artists</h1>
      <div className="flex justify-center">
        <div className="grid grid-cols-5 gap-6">
          {currentArtists.map((artist) => (
            <ArtistCard
              key={artist.id}
              artist={artist}
              onClick={() => handleArtistClick(artist)}
            />
          ))}
        </div>
      </div>
      <div className="flex justify-center mt-6">
        <button
          onClick={() => paginate(currentPage - 1)}
          disabled={currentPage === 1}
          className="px-4 py-2 mx-1 rounded bg-white text-red-600 disabled:opacity-50"
        >
          Previous
        </button>
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => paginate(i + 1)}
            className={`px-4 py-2 mx-1 rounded ${i + 1 === currentPage ? 'bg-red-600 text-white' : 'bg-white text-red-600'}`}
          >
            {i + 1}
          </button>
        ))}
        <button
          onClick={() => paginate(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="px-4 py-2 mx-1 rounded bg-white text-red-600 disabled:opacity-50"
        >
          Next
        </button>
      </div>
      {selectedArtist && <ArtistModal artist={selectedArtist} isVisible={isModalVisible} onClose={closePopup} modalRef={modalRef} />}
    </div>
  );
};

export default Artists;
