import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const clientId = '7c6b8a3ea0e3406b9d8e5974be8b442b';
const clientSecret = '49aa02d0a85249279ae8ab73bbef28f5';

const getSpotifyToken = async () => {
  const response = await axios.post('https://accounts.spotify.com/api/token', null, {
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      'Authorization': `Basic ${btoa(`${clientId}:${clientSecret}`)}`
    },
    params: {
      grant_type: 'client_credentials'
    }
  });

  return response.data.access_token;
};

const getSpotifyTopTracks = async () => {
  const token = await getSpotifyToken();
  const response = await axios.get('https://api.spotify.com/v1/playlists/37i9dQZEVXbMDoHDwVN2tF/tracks', {
    headers: {
      'Authorization': `Bearer ${token}`
    },
    params: {
      market: 'US',
      limit: 50
    }
  });

  return response.data.items.map(item => ({
    name: item.track.name.toLowerCase(),
    artist: item.track.artists[0].name.toLowerCase(),
    image: item.track.album.images[1]?.url || '',
  }));
};

const Card = () => {
  const [tracks, setTracks] = useState([]);
  const [error, setError] = useState('');
  const tableRef = useRef(null);

  useEffect(() => {
    const fetchTracks = async () => {
      try {
        const lastFmResponse = await axios.get('http://ws.audioscrobbler.com/2.0/', {
          params: {
            method: 'chart.gettoptracks',
            api_key: '28c425ac5fe3a7288e17efe8d7f9f82c', // Replace with your Last.fm API key
            format: 'json',
            limit: 50
          }
        });

        const spotifyTracks = await getSpotifyTopTracks();

        const lastFmTracks = lastFmResponse.data.tracks.track.map(track => ({
          ...track,
          name: track.name.toLowerCase(),
          artist: track.artist.name.toLowerCase(),
        }));

        const combinedTracks = lastFmTracks.map(lastFmTrack => {
          const spotifyTrack = spotifyTracks.find(
            spotifyTrack => 
              spotifyTrack.name.includes(lastFmTrack.name) || 
              lastFmTrack.name.includes(spotifyTrack.name)
          );
          return {
            ...lastFmTrack,
            image: spotifyTrack ? spotifyTrack.image : lastFmTrack.image[2]['#text']
          };
        });

        setTracks(combinedTracks);
        setError('');
      } catch (err) {
        setError('Failed to fetch top songs');
      }
    };
    fetchTracks();
  }, []);

  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const topSixTracks = tracks.slice(0, 6);
  const remainingTracks = tracks.slice(6, 17); // Limit to 15 more tracks

  const handleScroll = (e) => {
    if (tableRef.current && tableRef.current.contains(e.target)) {
      e.stopPropagation();
    }
  };

  return (
    <div className="p-4" onWheel={handleScroll}>
      <h1 className="text-2xl font-bold text-center mb-4">Trending</h1>
      <div className="flex">
        {/* Top 6 tracks */}
        <div className="w-1/2 pr-4 grid grid-cols-3 gap-4">
          {topSixTracks.map((track, index) => (
            <div key={index} className="bg-white rounded-lg shadow-md overflow-hidden">
              <img src={track.image} alt={track.name} className="w-full h-48 object-cover" />
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2 truncate">
                  <span className="mr-2">{index + 1}.</span>{track.name}
                </h3>
                <p className="text-sm text-gray-600 truncate">{track.artist.name}</p>
                <p className="text-sm text-gray-500 mt-2">Streams: {formatNumber(track.playcount)}</p>
              </div>
            </div>
          ))}
        </div>
        {/* Remaining tracks table */}
        <div className="w-1/2 pl-4">
          <div
            ref={tableRef}
            className="bg-white rounded-lg shadow-md overflow-hidden"
            style={{ height: 'auto' }} // Make height auto to match the height of the left cards
          >
            <table className="w-full">
              <thead className="bg-gray-100">
                <tr>
                  <th className="px-4 py-2 text-left">#</th>
                  <th className="px-4 py-2 text-left"></th>
                  <th className="px-4 py-2 text-left">Name</th>
                  <th className="px-4 py-2 text-left">Artist</th>
                  <th className="px-4 py-2 text-right">Streams</th>
                </tr>
              </thead>
              <tbody>
                {remainingTracks.map((track, index) => (
                  <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                    <td className="px-4 py-2">{index + 7}</td>
                    <td className="px-4 py-2">
                      <img src={track.image} alt={track.name} className="w-8 h-8 object-cover" />
                    </td>
                    <td className="px-4 py-2 truncate">{track.name}</td>
                    <td className="px-4 py-2 truncate">{track.artist.name}</td>
                    <td className="px-4 py-2 text-right">{formatNumber(track.playcount)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
