import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useQuery } from 'react-query';
import { getSpotifyTopArtists } from './API';
import ArtistCard from './ArtistCard';
import ArtistModal from './ArtistModal';

const Artists = () => {
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const modalRef = useRef(null);

  const { data: artists, error, isLoading } = useQuery('topArtists', getSpotifyTopArtists);

  const handleArtistClick = useCallback((artist) => {
    setSelectedArtist(artist);
    setIsModalVisible(true);
  }, []);

  const closePopup = useCallback(() => {
    setIsModalVisible(false);
    setTimeout(() => {
      setSelectedArtist(null);
    }, 300);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closePopup();
      }
    };

    if (isModalVisible) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isModalVisible, closePopup]);

  if (error) return <p className="text-red-500 text-center mt-4">Failed to fetch top artists</p>;
  if (isLoading) return <p className="text-center mt-4">Loading...</p>;

  const sortedArtists = [...artists].sort((a, b) => b.popularity - a.popularity);

  return (
    <div className="p-4 mb-3">
      <h1 className="text-5xl font-bold text-center mb-5 text-red-600">Top Artists</h1>
      <div className="grid grid-cols-3 gap-4">
        {sortedArtists.map((artist, index) => (
          <ArtistCard key={artist.id} artist={artist} index={index} onClick={handleArtistClick} />
        ))}
      </div>
      {selectedArtist && <ArtistModal artist={selectedArtist} isVisible={isModalVisible} onClose={closePopup} modalRef={modalRef} />}
    </div>
  );
};

export default Artists;
