import React from 'react';

const TrackModal = ({ track, isVisible, onClose, modalRef }) => {
  const formatNumber = (num) => num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  const capitalize = (str) => str.charAt(0).toUpperCase() + str.slice(1);

  return (
    <div
      className={`fixed inset-0 bg-black transition-all duration-300 ease-in-out ${
        isVisible ? 'bg-opacity-50 backdrop-blur-sm' : 'bg-opacity-0 backdrop-blur-none pointer-events-none'
      } flex items-center justify-center z-50`}
    >
      <div
        ref={modalRef}
        className={`bg-white p-8 rounded-lg shadow-xl w-full max-w-4xl relative transition-all duration-300 ease-in-out ${
          isVisible ? 'scale-100 opacity-100' : 'scale-0 opacity-0'
        }`}
      >
        <button onClick={onClose} className="absolute top-4 right-4 text-gray-600 hover:text-gray-800">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
        <div className="flex">
          <div className="w-1/2 pr-6">
            <h2 className="text-3xl font-bold mb-4">{track.name}</h2>
            <p className="text-xl mb-2"><strong>Artist:</strong> {track.artist}</p>
            {track.trackDetails?.album?.name !== track.name && track.trackDetails?.album?.name && (
              <p className="text-xl mb-2"><strong>Album:</strong> {track.trackDetails?.album?.name}</p>
            )}
            <p className="text-xl mb-6"><strong>Streams:</strong> {formatNumber(track.playcount)}</p>
            <iframe
              src={`https://open.spotify.com/embed/track/${track.id}`}
              width="100%"
              height="152"
              frameBorder="0"
              allowtransparency="true"
              allow="encrypted-media"
              className="mb-6 rounded"
              title="Track Player"
            ></iframe>
            <div className="mb-4">
              <h3 className="text-2xl font-semibold mb-2">Additional Stats</h3>
              <ul className="list-disc pl-5 text-lg">
                {track.trackDetails?.album?.release_date && (
                  <li><strong>Release Date:</strong> {track.trackDetails?.album?.release_date}</li>
                )}
                {track.trackDetails?.duration_ms && (
                  <li>
                    <strong>Duration:</strong>{' '}
                    {`${Math.floor(track.trackDetails.duration_ms / 60000)}:${Math.floor((track.trackDetails.duration_ms % 60000) / 1000)
                      .toString()
                      .padStart(2, '0')}`}
                  </li>
                )}
                {track.artistDetails?.genres?.length > 0 && (
                  <li>
                    <strong>Genre:</strong> {track.artistDetails.genres.map(capitalize).join(', ')}
                  </li>
                )}
                {track.trackDetails?.popularity && (
                  <li><strong>Popularity:</strong> {track.trackDetails.popularity}</li>
                )}
              </ul>
            </div>
          </div>
          <div className="w-1/2 pl-6 flex flex-col">
            <div className="mb-6">
              <img src={track.artistDetails?.images?.[0]?.url || ''} alt={track.artist} className="w-40 h-40 rounded-full mx-auto" loading="lazy" />
            </div>
            {track.trackDetails?.album && track.trackDetails.album.id && (
              <iframe
                src={`https://open.spotify.com/embed/album/${track.trackDetails.album.id}`}
                width="100%"
                height="380"
                frameBorder="0"
                allowtransparency="true"
                allow="encrypted-media"
                className="rounded flex-grow"
                title="Album Player"
              ></iframe>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TrackModal;
