import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useQuery } from 'react-query';
import { getSpotifyTopTracks, getLastFmStreams, getTrackDetails, getArtistDetails } from './API';

const Trending = () => {
  const [selectedTrack, setSelectedTrack] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const modalRef = useRef(null);

  const { data: tracks, error, isLoading } = useQuery('topTracks', async () => {
    const spotifyTracks = await getSpotifyTopTracks();
    return Promise.all(spotifyTracks.map(async (spotifyTrack) => {
      const playcount = await getLastFmStreams(spotifyTrack.name, spotifyTrack.artist);
      return { ...spotifyTrack, playcount };
    }));
  });

  const handleTrackClick = useCallback(async (track) => {
    setSelectedTrack(track);
    try {
      const [trackDetails, artistDetails] = await Promise.all([
        getTrackDetails(track.id),
        getArtistDetails(track.artistId)
      ]);
      setSelectedTrack(prev => ({ ...prev, trackDetails, artistDetails }));
    } catch (err) {
      console.error('Failed to fetch track or artist details', err);
    }
    setIsModalVisible(true);
  }, []);

  const closePopup = useCallback(() => {
    setIsModalVisible(false);
    setTimeout(() => {
      setSelectedTrack(null);
    }, 300);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closePopup();
      }
    };

    if (isModalVisible) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isModalVisible, closePopup]);

  const formatNumber = useCallback((num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  }, []);

  if (error) return <p className="text-red-500 text-center mt-4">Failed to fetch top songs</p>;
  if (isLoading) return <p className="text-center mt-4">Loading...</p>;

  const topSixTracks = tracks.slice(0, 6);
  const remainingTracks = tracks.slice(6, 19);

  return (
    <div className="p-4">
      <h1 className="text-5xl font-bold text-center mb-4 text-red-600">Trending</h1>
      <div className="flex">
        <div className="w-1/2 pr-4 grid grid-cols-3 gap-4">
          {topSixTracks.map((track, index) => (
            <div 
              key={track.id} 
              className="bg-white rounded-lg shadow-md overflow-hidden cursor-pointer transition-all duration-150 ease-in-out transform hover:-translate-y-1 hover:shadow-lg hover:shadow-glow hover:bg-gray-100"
              onClick={() => handleTrackClick(track)}
            >
              <img src={track.image} alt={track.name} className="w-full h-auto object-contain" loading="lazy" />
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2 truncate">
                  <span className="mr-2">{index + 1}.</span>{track.name}
                </h3>
                <p className="text-sm text-red-600 truncate">{track.artist}</p>
                <p className="text-sm text-black-500 mt-2">Streams: {formatNumber(track.playcount)}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="w-1/2 pl-4">
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <table className="w-full">
              <thead className="bg-zinc-900/100 text-accent">
                <tr>
                  <th className="px-4 py-2 text-left">#</th>
                  <th className="px-4 py-2 text-left"></th>
                  <th className="px-4 py-2 text-left">Name</th>
                  <th className="px-4 py-2 text-left">Artist</th>
                  <th className="px-4 py-2 text-right">Streams</th>
                </tr>
              </thead>
              <tbody>
                {remainingTracks.map((track, index) => (
                  <tr 
                    key={track.id} 
                    className={`${index % 2 === 0 ? 'bg-primary/20' : 'bg-accent/10'} hover:bg-gray-200 cursor-pointer transition-colors duration-300`}
                    onClick={() => handleTrackClick(track)}
                  >
                    <td className="px-4 py-2">{index + 7}</td>
                    <td className="px-4 py-2">
                      <img src={track.image} alt={track.name} className="w-8 h-8 object-contain" loading="lazy" />
                    </td>
                    <td className="px-4 py-2 truncate">{track.name}</td>
                    <td className="px-4 py-2 truncate">{track.artist}</td>
                    <td className="px-4 py-2 text-right">{formatNumber(track.playcount)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {selectedTrack && (
        <div 
          className={`fixed inset-0 bg-black transition-all duration-300 ease-in-out ${
            isModalVisible ? 'bg-opacity-50 backdrop-blur-sm' : 'bg-opacity-0 backdrop-blur-none pointer-events-none'
          } flex items-center justify-center z-50`}
        >
          <div 
            ref={modalRef} 
            className={`bg-white p-8 rounded-lg shadow-xl w-full max-w-4xl relative transition-all duration-300 ease-in-out ${
              isModalVisible ? 'scale-100 opacity-100' : 'scale-0 opacity-0'
            }`}
          >
            <button 
              onClick={closePopup}
              className="absolute top-4 right-4 text-gray-600 hover:text-gray-800"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
            <div className="flex">
              <div className="w-1/2 pr-6">
                <h2 className="text-3xl font-bold mb-4">{selectedTrack.name}</h2>
                <p className="text-xl mb-2"><strong>Artist:</strong> {selectedTrack.artist}</p>
                {selectedTrack.trackDetails?.album?.name !== selectedTrack.name && (
                  <p className="text-xl mb-2"><strong>Album:</strong> {selectedTrack.trackDetails?.album?.name || 'N/A'}</p>
                )}
                <p className="text-xl mb-6"><strong>Streams:</strong> {formatNumber(selectedTrack.playcount)}</p>
                <iframe
                  src={`https://open.spotify.com/embed/track/${selectedTrack.id}`}
                  width="100%"
                  height="152"
                  frameBorder="0"
                  allowtransparency="true"
                  allow="encrypted-media"
                  className="mb-6 rounded"
                  title="Track Player"
                ></iframe>
                <div className="mb-4">
                  <h3 className="text-2xl font-semibold mb-2">Additional Stats</h3>
                  <ul className="list-disc pl-5 text-lg">
                    <li><strong>Release Date:</strong> {selectedTrack.trackDetails?.album?.release_date || 'N/A'}</li>
                    <li><strong>Duration:</strong> {selectedTrack.trackDetails?.duration_ms ? `${Math.floor(selectedTrack.trackDetails.duration_ms / 60000)}:${Math.floor((selectedTrack.trackDetails.duration_ms % 60000) / 1000).toString().padStart(2, '0')}` : 'N/A'}</li>
                    <li><strong>Genre:</strong> {selectedTrack.artistDetails?.genres?.join(', ') || 'N/A'}</li>
                    <li><strong>Popularity:</strong> {selectedTrack.trackDetails?.popularity || 'N/A'}</li>
                  </ul>
                </div>
              </div>
              <div className="w-1/2 pl-6 flex flex-col">
                <div className="mb-6">
                  <img src={selectedTrack.artistDetails?.images?.[0]?.url || ''} alt={selectedTrack.artist} className="w-40 h-40 rounded-full mx-auto" loading="lazy" />
                </div>
                {selectedTrack.trackDetails?.album && selectedTrack.trackDetails.album.id && (
                  <iframe
                    src={`https://open.spotify.com/embed/album/${selectedTrack.trackDetails.album.id}`}
                    width="100%"
                    height="380"
                    frameBorder="0"
                    allowtransparency="true"
                    allow="encrypted-media"
                    className="rounded flex-grow"
                    title="Album Player"
                  ></iframe>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default React.memo(Trending);
